import React from "react";

const AgregarTrabajos = () => {
  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-success">
      <div
        className="card p-4 p-md-2 shadow-lg text-center"
        style={{
          width: "100%",
          height: "90vh",
          maxWidth: "1200px",
          borderRadius: "20px",
          overflow: "hidden",
        }}
      >
        {/* Header */}
        <div className="d-flex justify-content-between align-items-center mb-3">
          <img
            src="/images/logosdc.jpg"
            alt="Logo SDC"
            style={{
              width: "100px",
              height: "100px",
              borderRadius: "50%",
            }}
          />
          <h1
            className="flex-grow-1 text-black text-center m-0"
            style={{
              fontSize: "24px",
            }}
          >
            Progreso
          </h1>
          <i
            className="fas fa-bars"
            style={{
              fontSize: "30px",
            }}
          ></i>
        </div>

        {/* Contenedor con scroll horizontal */}
        <div
          className="overflow-auto"
          style={{
            maxHeight: "75vh", // La altura máxima para scroll vertical si es necesario
            overflowX: "auto", // Permite el desplazamiento horizontal
            whiteSpace: "nowrap", // Evita que las celdas se rompan en varias líneas
            paddingRight: "10px",
          }}
        >
          {/* Encabezados */}
          <div
            className="row m-0 bg-light text-center"
            style={{
              borderBottom: "1px solid #ccc",
              display: "inline-flex", // Usamos inline-flex para que las columnas no se ajusten
              minWidth: "100%",
            }}
          >
            <div
              className="col py-2 border-end"
              style={{ minWidth: "200px", backgroundColor: "#eaeaea" }}
            >
              Por hacer
            </div>
            <div
              className="col py-2 border-end"
              style={{ minWidth: "200px", backgroundColor: "#eaeaea" }}
            >
              En Progreso
            </div>
            <div
              className="col py-2 border-end"
              style={{ minWidth: "200px", backgroundColor: "#eaeaea" }}
            >
              Por cobrar
            </div>
            <div
              className="col py-2 border-end"
              style={{ minWidth: "200px", backgroundColor: "#eaeaea" }}
            >
              Para facturar
            </div>
            <div
              className="col py-2"
              style={{ minWidth: "200px", backgroundColor: "#eaeaea" }}
            >
              Facturado
            </div>
          </div>

          {/* Filas de contenido */}
          <div
            className="row m-0"
            style={{
              display: "inline-flex", // Esto hace que las celdas estén en una sola fila sin romperse
            }}
          >
            {/* Por hacer */}
            <div
              className="col bg-danger text-white text-center py-4"
              style={{ minWidth: "200px" }}
            >
              <p>Contenido aquí</p>
            </div>

            {/* En progreso */}
            <div
              className="col bg-warning text-dark text-center py-4"
              style={{ minWidth: "200px" }}
            >
              <p>Contenido aquí</p>
            </div>

            {/* Por cobrar */}
            <div
              className="col bg-info text-white text-center py-4"
              style={{ minWidth: "200px" }}
            >
              <p>Contenido aquí</p>
            </div>

            {/* Para facturar */}
            <div
              className="col bg-success text-white text-center py-4"
              style={{ minWidth: "200px" }}
            >
              <p>Contenido aquí</p>
            </div>

            {/* Facturado */}
            <div
              className="col bg-primary text-white text-center py-4"
              style={{ minWidth: "200px" }}
            >
              <p>Contenido aquí</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AgregarTrabajos;
