import React from "react";

const AgregarTrabajos = () => {
  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-success"style={{ height: "100px" }}>
      <div className="card p-4 p-md-2 shadow-lg text-center" 
      style={{ 
        maxWidth: "1200px", 
        width: "100%", 
        height: "90vh",
        borderRadius:"20px",
        overflow:"hidden"
        }}
        >
          <div className="d-flex justify-content-between align-items-center mb-3">
            <img src="/images/logosdc.jpg" alt="Logo SDC" 
            style={{
              width: "100px",
              height: "100px",
              borderRadius: "50%"
            }}/>
            <h1 className="flex-grow-1 text-black text-center m-0 " style={{
              fontSize: "24"
              }}>Progreso</h1>
            <i className="fas fa-bars" style={{
              fontSize: "30px"
            }}></i>
          </div>

        <div style={{
          overflowY: "auto",
          maxHeight: "75vh",
          paddingRight: "10px"  
        }}  >
          <div className="row m-0 bg-light text-cetner"
          style={{ borderBottom: "1px solid #ccc" }}>
            <div className="col py-2 border-end">Por hacer</div>
            <div className="col py-2 border-end">En Progreso</div>
            <div className="col py-2 border-end">Por cobrar</div>
            <div className="col py-2 border-end">Para facturar</div>
            <div className="col py-2">Facturado</div>
          </div>
            {/* Contenido */}
  <div className="row m-0">
    {/* Por hacer */}
    <div className="col bg-danger text-white text-center py-4">
      <p>Contenido aquí</p>
    </div>

    {/* En progreso */}
    <div className="col bg-warning text-dark text-center py-4">
      <p>Contenido aquí</p>
    </div>

    {/* Por cobrar */}
    <div className="col bg-info text-white text-center py-4">
      <p>Contenido aquí</p>
    </div>

    {/* Para facturar */}
    <div className="col bg-success text-white text-center py-4">
      <p>Contenido aquí</p>
    </div>

    {/* Facturado */}
    <div className="col bg-primary text-white text-center py-4">
      <p>Contenido aquí</p>
    </div>
  </div>
        </div>
      </div>
    </div>
  );
};

export default AgregarTrabajos;
