import React, { useState } from "react";

const Amojonamientos = () => {

  const [activeSection, setActiveSection] = useState(null);

  const handleSectionToggle = (section) => {
    setActiveSection((prev) => (prev === section ? null: section));
  }

  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-success">
      <div
        className="card p-4 p-md-2 shadow-lg text-center"
        style={{
          width: "100%",  // Esto asegura que el contenedor ocupe todo el ancho disponible en pantalla.
          height: "90vh", // La altura sigue siendo un porcentaje de la altura del viewport.
          maxWidth: "1200px", // Mantiene la proporción del contenedor sin que se estire más allá de este ancho
          borderRadius: "20px",
          overflow: "hidden",
        }}
      >
        {/* Header */}
        <div className="d-flex justify-content-between align-items-center mb-3">
          <img
            src="/images/logosdc.jpg"
            alt="Logo SDC"
            style={{
              width: "100px",
              height: "100px",
              borderRadius: "50%",
            }}
          />
          <h1
            className="flex-grow-1 text-black text-center m-0"
            style={{
              fontSize: "24px",
            }}
          >
            Progreso
          </h1>
          <i
            className="fas fa-bars"
            style={{
              fontSize: "30px",
            }}
          ></i>
        </div>

        {/* Contenedor con scroll horizontal */}
        <div
          className="overflow-auto"
          style={{
            maxHeight: "75vh", // Se mantiene la altura máxima para el scroll vertical si es necesario
            overflowX: "auto", // Permite el desplazamiento horizontal si el contenido excede el contenedor
            whiteSpace: "nowrap", // Evita que el contenido se rompa en varias líneas
            paddingRight: "10px",
          }}
        >
          {/* Encabezados */}
          <div
            className="row m-0 bg-light text-center"
            style={{
              borderBottom: "1px solid #ccc",
              display: "flex", // Necesario para que los encabezados no se ajusten
              minWidth: "100%",
            }}
          >
            <div
              className="col py-2 border-end"
              style={{ minWidth: "200px", backgroundColor: "#eaeaea" }}
            >
              Por hacer
            </div>
            <div
              className="col py-2 border-end"
              style={{ minWidth: "200px", backgroundColor: "#eaeaea" }}
            >
              En Progreso
            </div>
            <div
              className="col py-2 border-end"
              style={{ minWidth: "200px", backgroundColor: "#eaeaea" }}
            >
              Por cobrar
            </div>
            <div
              className="col py-2 border-end"
              style={{ minWidth: "200px", backgroundColor: "#eaeaea" }}
            >
              Para facturar
            </div>
            <div
              className="col py-2"
              style={{ minWidth: "200px", backgroundColor: "#eaeaea" }}
            >
              Facturado
            </div>
          </div>

          {/* Filas de contenido */}
          <div
            className="row m-0"
            style={{
              display: "flex", // Flexbox para evitar que las columnas se ajusten
            }}
          >
            {/* Por hacer */}
            <div
              className="col bg-danger text-white text-center py-4"
              style={{ minWidth: "200px" }}
            >
              <p>Contenido aquí</p>
            </div>

            {/* En progreso */}
            <div
              className="col bg-warning text-dark text-center py-4"
              style={{ minWidth: "200px" }}
            >
              <p>Contenido aquí</p>
            </div>

            {/* Por cobrar */}
            <div
              className="col bg-info text-white text-center py-4"
              style={{ minWidth: "200px" }}
            >
              <p>Contenido aquí</p>
            </div>

            {/* Para facturar */}
            <div
              className="col bg-success text-white text-center py-4"
              style={{ minWidth: "200px" }}
            >
              <p>Contenido aquí</p>
            </div>

            {/* Facturado */}
            <div
              className="col bg-primary text-white text-center py-4"
              style={{ minWidth: "200px" }}
            >
              <p>Contenido aquí</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Amojonamientos;
